}

sources.sass\\main.scss:
/*
 * Главный файл стилей.
 * Все паршалы подключаются сюда.
 */
// ================= COMMON STYLES ========================

@import '../sass/utils/variables';
@import '../sass/utils/reset.scss';
@import '../sass/utils/reset';
@import '../sass/utils/container';
@import '../sass/utils/visually-hidden';
@import '../sass/utils/breakpoints';
@import '../sass/utils/mixins/respond';

// ===================== LAYOUTS =============================

@import '../sass/layout/header';
@import '../sass/layout/hero';
@import '../sass/components/header-btn';
@import '../sass/components/menu';
@import '../sass/layout/products';
@import './layout/about';
@import './layout/advantages';
@import '../sass/layout/gallery';
@import '../sass/layout/customer_reviews';
@import '../sass/layout/contacts';
@import '../sass/layout/footer';

sources.sass\\utils\\_reset.scss:
body {
  font-family: $content-font;
}

h1,
h2,
h3,
h4,
p {
  margin: 0;
}

ul,
ol {
  margin: 0;
  padding: 0;
  list-style: none;
}

img {
  display: block;
  max-width: 100%;
  height: auto;
}


.link,
a {text-decoration: none;}


.list {
  list-style: none;
  text-decoration: none;
}

img {
  display: block;
}

button {
  cursor: pointer;
  border: none;
  background: none;
}

a {
  list-style: none;
  text-decoration: none;
}

li {
  list-style-type: none;
}

sources.sass\\utils\\_variables.scss:
// ============ colors ================
$main-brand-color: #ffb8ca;
$secondary-brand-color: #fff5f6;
$light-backgrornd-color: #e5e5e5;
$brand-btn-color: #d41342;
$light-btn-hover: #ecebeb;
$dark-btn-hover: #c7133e;
$coffee-card-color: #f0d1a5;
$green-color: #c2e297;

// ========== fonts colors =============
$main-font-color: #d41443;
$secondary-font-color: #ffa5ba;
$dark-font-color: #907e82;

// ============== fonts =================
$content-font: 'DM Sans', sans-serif;
$decorative-font: 'Titan One';

// =========== time function =============
$main-transition: 250ms cubic-bezier(0.4, 0, 0.2, 1);

sources.sass\\utils\\_container.scss:
.container {
  width: 100%;
  padding-left: 10px;
  padding-right: 10px;
  margin-left: auto;
  margin-right: auto;
  @media screen and (min-width: 480px) {
    width: 300px;
  }
  @media screen and (min-width: 768px) {
    width: 720px;
  }
  @media screen and (min-width: 1280px) {
    width: 1080px;
    padding-left: 15px;
    padding-right: 15px;
  }
}

sources.sass\\utils\\_visually-hidden.scss:
.visually-hidden {
  position: absolute;
  width: 1px;
  height: 1px;
  margin: -1px;
  border: 0;
  padding: 0;

  white-space: nowrap;
  clip-path: inset(100%);
  clip: rect(0 0 0 0);
  overflow: hidden;
}

sources.sass\\layout\\_header.scss:
.header {
  background-color: $main-brand-color;
  position: relative;
  display: flex;
  align-items: center;
  padding-top: 6px;
  justify-content: space-between;

  @media screen and(min-width: 768px) {
    padding-top: 37px;
  }
  // @media screen and(min-width: 1200px) {
  //   max-width: 1280px;
  // }
}

.menu {
  display: none;
  @media screen and(min-width: 1200px) {
    display: flex;
    align-items: center;
  }
}

.navigation-list {
  display: flex;
}

.navigation-list__item {
  &:not(:last-child) {
    margin-right: 35px;
  }
}

.navigation-link {
  padding: 50px 0;
  font-size: 16px;
  font-weight: 1.31;
  font-weight: 700;
  color: #ffff;
  // transition: color 250ms var(--timing-function);

  &:hover,
  &:focus {
    color: $main-font-color;
  }
}

.active-navigation-link {
  color: $main-font-color;
}

.navigation {
  display: flex;
  align-items: center;
  justify-content: space-between;
}
.site-nav {
  display: block;
  // @media screen and (min-width: 768px) {
  //   display: flex;
  //   margin-right: 84px;
  // }
  @media screen and (min-width: 1200px) {
    display: flex;
    margin-right: 110px;
    margin-left: 110px;
  }
}

.site-nav__item {
  padding-top: 13px;
  padding-bottom: 11px;
  padding-left: 21px;
  // width: 100%;
  @media screen and (min-width: 1200px) {
    margin-top: 0;
    margin-bottom: 0;
    padding: 0;
    outline: none;
    &:not(:last-child) {
      margin-right: 50px;
      margin-bottom: 0;
    }
  }
}

.site-nav__link {
  outline: 1px solid #e18298;
  font-weight: 700;
  font-size: 14px;
  line-height: 1.28;
  color: #ffffff;
  display: block;

  &--active {
    color: $main-font-color;
  }
}

sources.sass\\layout\\_hero.scss:
.hero {
  background-color: $main-brand-color;
  position: relative;
}

.hero .container {
  height: 446px;
  padding-top: 137px;
  padding-bottom: 160px;
  position: relative;
  overflow: clip;

  @media screen and(min-width: 768px) {
    height: 312px;
    padding-top: 15px;
    overflow: visible;
  }
  @media screen and(min-width: 1200px) {
    height: 578px;
    padding-top: 84px;
    max-width: 1280px;
  }
}
.hero__title {
  width: 218px;
  font-family: $decorative-font;
  font-weight: 400;
  font-size: 26px;
  line-height: 1.14;
  letter-spacing: 0.04em;
  text-transform: uppercase;
  color: #ffffff;
  // z-index: 2;

  @media screen and(min-width: 768px) {
    font-size: 22px;
    width: 162px;
  }

  @media screen and(min-width: 1200px) {
    font-size: 38px;
    width: 277px;
  }
}

.hero__title--colored {
  font-family: Titan One;
  font-weight: 400;
  font-size: 48px;
  line-height: 0.96;
  text-transform: uppercase;
  color: $brand-btn-color;

  @media screen and(min-width: 768px) {
    font-size: 34px;
    line-height: 0.9;
  }
  @media screen and(min-width: 1200px) {
    font-size: 58px;
  }
}
.hero-subtitle {
  position: absolute;
  bottom: 59px;
  left: 154px;
  // z-index: 2;
  font-size: 8px;
  line-height: 1.5;
  color: #ffffff;
  width: 114px;

  @media screen and(min-width: 1200px) {
    left: 295px;
    bottom: 102px;
    font-size: 14px;
    line-height: 1.4;
    width: 200px;
  }
}

.hero__btn-list {
  display: flex;
  margin-top: 20px;

  @media screen and(min-width: 1200px) {
    margin-top: 25px;
  }
}

.hero__btn-item {
  &:not(:last-child) {
    margin-right: 8px;

    @media screen and(min-width: 1200px) {
      margin-right: 15px;
    }
  }
  z-index: 1002;
}

.ellipse {
  display: block;
  position: absolute;
  left: 36px;
  bottom: 75px;
  border-radius: 50%;
  width: 324px;
  height: 324px;
  background-color: var(--color-pink-dark);

  @media screen and(min-width: 768px) {
    left: 307px;
    bottom: 46px;
  }

  @media screen and(min-width: 1200px) {
    width: 538px;
    height: 538px;
    left: 475px;
    bottom: 75px;
  }
}
.ice-cream {
  position: absolute;
  bottom: 0;
  left: 117px;
  z-index: 2;

  @media screen and(min-width: 768px) {
    left: 378px;
  }

  @media screen and(min-width: 1200px) {
    left: 594px;
  }
}

sources.sass\\components\\_header-btn.scss:
.btn {
  display: block;
  background-color: #ffffff;
  font-weight: 700;
  font-size: 16px;
  line-height: 1.31;
  align-items: center;
  text-align: center;
  letter-spacing: 0.06em;
  color: $brand-btn-color;
  font-family: inherit;
  border: 0;
  border-radius: 22px;
  padding: 12px 37px;
  transition: background-color 250ms cubic-bezier(0.4, 0, 0.2, 1);

  &:hover,
  &:focus {
    background-color: $light-btn-hover;
    cursor: pointer;
  }
}
.button--pink {
  background-color: $brand-btn-color;
  color: #ffffff;
  transition: background-color 250ms cubic-bezier(0.4, 0, 0.2, 1);

  &:hover,
  &:focus {
    background-color: $dark-btn-hover;
    cursor: pointer;
  }
  display: flex;
  font-size: 8px;
  font-weight: 400;
  line-height: 1.25;
  padding: 7px 10px;

  @media screen and(min-width: 1200px) {
    font-size: 14px;
    line-height: 1.29;
    padding: 11px 14px;
  }
  @media screen and(min-width: 1200px) {
    padding: 11px 20px;
  }
}

.button--white {
  display: flex;
  font-size: 8px;
  font-weight: 400;
  line-height: 1.25;
  padding: 7px 13px;
  @media screen and(min-width: 1200px) {
    font-size: 14px;
    line-height: 1.29;
    padding: 11px 14px;
  }
  @media screen and(min-width: 1200px) {
    padding: 11px 20px;
  }
  &:hover,
  &:focus {
    background-color: rgba(236, 235, 235, 1);
    cursor: pointer;
  }
}

.hero__btn-list {
  display: flex;
  margin-top: 20px;

  @media screen and(min-width: 1200px) {
    margin-top: 25px;
  }
}
.hero__btn-item {
  &:not(:last-child) {
    margin-right: 8px;

    @media screen and(min-width: 1200px) {
      margin-right: 15px;
    }
  }
  // z-index: 2;
}

.buttons {
  display: flex;
  justify-content: center;
  align-items: baseline;
}
.header-btn {
  margin-left: 30px;
  // z-index: 1;
  @media screen and(max-width: 767px) {
    display: none;
  }
}
.button__buy {
  width: 100%;
  margin-top: 24px;
}
.burger-btn {
  margin: 0 auto;
  // z-index: 3;
  cursor: pointer;
  background-color: transparent;
  border: none;
  padding: 0;
  @media screen and(min-width: 1200px) {
    display: none;
  }
}

sources.sass\\components\\_menu.scss:
.menu-button {
  position: absolute;
  top: 25px;
  right: 20px;
  display: inline-flex;
  margin: 0;
  padding: 0;
  // z-index: 1002;
  background-color: transparent;
  &.is-open {
    position: fixed;
  }
  @media screen and (min-width: 768px) {
    display: none;
  }
}
// .menu-button .mobile__close {
//   display: none;
// }
.menu-button.is-open .mobile__close {
  display: block;
  position: absolute;
  top: 25px;
  right: 20px;
}
.menu-button.is-open .mobile__burger {
  display: none;
}

.menu-container {
  background-color: #e17992;
  position: fixed;
  top: 0;
  right: 0;
  width: 100%;
  height: 100%;
  padding-top: 60px;
  width: 250px;
  margin-left: auto;
  transform: translateY(0) translateX(50%);
  opacity: 0;
  visibility: hidden;
  pointer-events: none;
  overflow: auto;
  z-index: 100;
  // transition: all 250ms var(--timing-function);
  box-shadow: -8px 0px 20px rgba(0, 0, 0, 0.25);

  &.is-open {
    opacity: 1;
    visibility: visible;
    pointer-events: auto;
    transform: translateY(0) translateX(0);
  }

  @media screen and(max-width: 767px) {
    width: 100vw;
    height: 100vh;
  }
}

sources.sass\\layout\\_products.scss:
.products {
  padding-top: 121px;
  padding-bottom: 98px;

  @media screen and (min-width: 768px) {
    padding-top: 122px;
    padding-bottom: 112px;
  }

  @media screen and (min-width: 1280px) {
    padding-top: 127px;
    padding-bottom: 119px;
  }
}

.products-text {
  margin-bottom: 15px;

  font-family: $decorative-font;
  color: $secondary-font-color;
  font-size: 18px;
  line-height: 1.16;
  text-align: center;
  text-transform: uppercase;

  @media screen and (min-width: 768px) {
    font-size: 26px;
  }

  @media screen and (min-width: 1280px) {
    font-size: 30px;
  }
}

.products-header {
  margin-bottom: 144px;

  font-family: $decorative-font;
  color: $main-font-color;
  font-size: 34px;
  line-height: 1.14;
  text-align: center;
  text-transform: uppercase;

  @media screen and (min-width: 768px) {
    font-size: 48px;
    margin-bottom: 150px;
  }

  @media screen and (min-width: 1280px) {
    font-size: 58px;
    margin-bottom: 259px;
  }
}

.products-title {
  position: relative;
  display: flex;
  justify-content: center;

  margin-bottom: 60px;

  font-family: $decorative-font;
  font-size: 20px;
  line-height: 1.15;
  text-align: center;
  letter-spacing: 0.04em;
  text-transform: uppercase;
  font-style: normal;
  font-weight: normal;
  color: #ffffff;

  @media screen and (min-width: 1280px) {
    font-size: 30px;
    margin-bottom: 70px;
  }
}

.products-title::after {
  content: '. . .';
  position: absolute;
  top: 30px;

  text-align: center;
  font-weight: 900;
  letter-spacing: 0.005rem;

  @media screen and (min-width: 1280px) {
    top: 45px;
  }
}

.products-list {
  list-style: none;

  @media screen and (min-width: 768px) {
    display: flex;
    margin: -10px;
  }

  @media screen and (min-width: 1280px) {
    margin: -15px;
  }
}

.products-list__item {
  min-width: 280px;
  min-height: 485px;

  &:not(:last-child) {
    @media screen and (max-width: 767px) {
      margin-bottom: 130px;
    }
  }

  @media screen and (min-width: 768px) {
    margin: 10px;
    flex-basis: calc((100% - 60px) / 3);
    min-width: 220px;
  }

  @media screen and (min-width: 1280px) {
    margin: 15px;
    flex-basis: calc((100% - 90px) / 3);
    min-width: 329px;
    min-height: 525px;
  }
}

.products-list__text {
  margin-bottom: 30px;
  font-family: $content-font;
  font-weight: bold;
  font-size: 14px;
  line-height: 152%;
  text-align: center;
  letter-spacing: 0.04em;
  color: #ffffff;

  @media screen and (min-width: 1280px) {
    font-size: 16px;
    margin-bottom: 62px;
  }
}

.ice-cream__card-wrap {
  background-color: $main-brand-color;
  border-radius: 24px;
  padding-top: 161px;
  padding-bottom: 40px;
  padding-left: 40px;
  padding-right: 40px;

  @media screen and (min-width: 1280px) {
    min-height: 525px;
    padding-top: 163px;
    padding-bottom: 50px;
    padding-left: 44px;
    padding-right: 44px;
  }
}
.products-title__icecream::before {
  content: '';
  background-image: url('../images/products/product1_mb.png');

  @media (min-device-pixel-ratio: 2),
    (-webkit-min-device-pixel-ratio: 2),
    (min-resolution: 192dpi),
    (min-resolution: 2dppx) {
    background-image: url('../images/products/product1_mb@2x.png');
  }
  background-repeat: no-repeat;
  background-position: center;
  background-size: cover;
  width: 252px;
  height: 249px;
  position: absolute;
  bottom: 45px;

  @media screen and (min-width: 768px) {
    background-image: url('../images/products/product1_tb.png');
    @media (min-device-pixel-ratio: 2),
      (-webkit-min-device-pixel-ratio: 2),
      (min-resolution: 192dpi),
      (min-resolution: 2dppx) {
      background-image: url('../images/products/product1_mb@2x.png');
    }
    width: 220px;
    height: 226px;
  }

  @media screen and (min-width: 1280px) {
    background-image: url('../images/products/product1_ds.png');
    @media (min-device-pixel-ratio: 2),
      (-webkit-min-device-pixel-ratio: 2),
      (min-resolution: 192dpi),
      (min-resolution: 2dppx) {
      background-image: url('../images/products/product1_ds@2x.png');
    }
    width: 335px;
    height: 335px;
    bottom: 65px;
  }
}

.icecoffee__card-wrap {
  background-color: $coffee-card-color;
  border-radius: 24px;

  padding-top: 161px;
  padding-bottom: 40px;
  padding-left: 40px;
  padding-right: 40px;

  @media screen and (min-width: 1280px) {
    min-height: 525px;
    padding-top: 163px;
    padding-bottom: 50px;
    padding-left: 44px;
    padding-right: 44px;
  }
}

.products-title__icecoffee::before {
  content: '';
  background-image: url('../images/products/product2_mb.png');

  @media (min-device-pixel-ratio: 2),
    (-webkit-min-device-pixel-ratio: 2),
    (min-resolution: 192dpi),
    (min-resolution: 2dppx) {
    background-image: url('../images/products/product2_mb@2x.png');
  }
  background-repeat: no-repeat;
  background-position: center;
  background-size: cover;
  width: 252px;
  height: 249px;
  position: absolute;
  bottom: 45px;

  @media screen and (min-width: 768px) {
    background-image: url('../images/products/product2_tb.png');
    @media (min-device-pixel-ratio: 2),
      (-webkit-min-device-pixel-ratio: 2),
      (min-resolution: 192dpi),
      (min-resolution: 2dppx) {
      background-image: url('../images/products/product2_tb@2x.png');
    }
    width: 220px;
    height: 233px;
  }

  @media screen and (min-width: 1280px) {
    background-image: url('../images/products/product2_ds.png');
    @media (min-device-pixel-ratio: 2),
      (-webkit-min-device-pixel-ratio: 2),
      (min-resolution: 192dpi),
      (min-resolution: 2dppx) {
      background-image: url('../images/products/product2_ds@2x.png');
    }
    width: 335px;
    height: 337px;
    bottom: 65px;
  }
}

.milkshakes__card-wrap {
  background-color: $green-color;
  border-radius: 24px;
  padding: 161px 40px 40px;

  @media screen and (min-width: 1280px) {
