sources.sass\\layout\\_about.scss:
.about {
  background-color: $secondary-brand-color;
  padding-top: 127px;
  padding-bottom: 39px;
}

.about__label {
  font-family: $decorative-font;
  font-weight: 400;
  font-size: 18px;
  line-height: 1.16;
  text-align: center;
  text-transform: uppercase;
  color: $secondary-font-color;
  margin-bottom: 15px;
}

.about__title {
  font-family: $decorative-font;
  font-weight: 400;
  font-size: 34px;
  line-height: 1.14;
  text-align: center;
  letter-spacing: 0.04em;
  text-transform: uppercase;
  color: $main-font-color;
  margin-bottom: 26px;
}

.about__content {
  display: flex;
  flex-direction: column;
  margin: 0 auto;
}

.about__description {
  padding-left: 40px;
  padding-right: 40px;
}
.about__text-up {
  font-family: $content-font;
  font-weight: 500;
  font-size: 12px;
  line-height: 1.62;
  color: #000000;
  width: 220px;
  margin-bottom: 35px;
}

.about__text-down {
  font-family: $content-font;
  font-weight: 500;
  font-size: 12px;
  line-height: 1.62;
  color: #907e82;
  margin-bottom: 17px;
  width: 220px;
}

.about__button {
  width: 176px;
  height: 44px;
  border-radius: 22px;
  background-color: #ffffff;
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
}
.about__span {
  font-family: $content-font;
  font-weight: 700;
  font-size: 14px;
  line-height: 1.28;
  text-align: center;
  color: $main-font-color;
  margin-right: 13px;
}

@media screen and (max-width: 479px) {
  .about__container {
    max-width: 300px;
  }
}

@media screen and (max-width: 767px) {
  .about-advantages {
    background-image: url(../images/about/bg/section-bg-mobile.png);
    background-repeat: no-repeat;
    background-position: center;
    background-size: contain;
    background-attachment: fixed;
    @media (max-width: 479px) and (min-device-pixel-ratio: 2),
      (min-resolution: 192dpi),
      (min-resolution: 2dppx) {
      background-image: url(../images/about/bg/section-bg-mobile@2x.png);
    }
  }
}

@media screen and (min-width: 768px) {
  .about {
    padding-top: 130px;
    padding-bottom: 66px;
  }

  .about__content {
    flex-direction: row;
  }

  .about__label {
    font-size: 26px;
  }

  .about__title {
    font-size: 48px;
    margin-bottom: 50px;
  }

  .about__description {
    padding-top: 50px;
    padding-left: 17px;
    padding-right: 0;
  }

  .about__text-up,
  .about__text-down {
    font-size: 14px;
    line-height: 1.76;
    width: 340px;
  }

  .about__text-down {
    margin-bottom: 62px;
  }
}

@media screen and (min-width: 1280px) {
  .about {
    padding-top: 125px;
    padding-bottom: 39px;
  }
  .about-advantages {
    background-image: url(../images/about/bg/section-bg.png);
    background-repeat: no-repeat;
    background-position: center;
    background-size: contain;
    background-attachment: fixed;
    @media (min-width: 1280px) and (min-device-pixel-ratio: 2),
      (min-resolution: 192dpi),
      (min-resolution: 2dppx) {
      background-image: url(../images/about/bg/section-bg@2x.png);
    }
  }

  .about__label {
    font-size: 30px;
    line-height: 1.13;
  }

  .about__title {
    font-size: 58px;
    line-height: 1.13;
  }

  .about__description {
    padding-left: 74px;
  }
  .about__text-up {
    font-size: 16px;
    line-height: 1.76;
    width: 455px;
  }
  .about__text-down {
    font-size: 14px;
    line-height: 1.94;
    margin-bottom: 45px;
    width: 455px;
  }
}

//backdrop

.about__backdrop {
  position: fixed;
  top: 0;
  left: 0;
  background-color: rgba(0, 0, 0, 0.2);
  width: 100%;
  height: 200%;
  opacity: 1;
  transition: opacity, transform, $main-transition;
  z-index: 100;
}

.about__backdrop.about__is-hidden {
  opacity: 0;
  visibility: hidden;
  pointer-events: none;
}

.about__cloused {
  border-radius: 50%;
  border: 1px solid rgba(0, 0, 0, 0.1);
  box-sizing: border-box;
  background-color: #ffffff;

  width: 30px;
  height: 30px;

  position: absolute;
  top: 8px;
  right: 8px;

  display: flex;
  justify-content: center;
  align-items: center;
  margin: 0;
  padding: 0;

  cursor: pointer;
}

.about__cloused svg {
  color: #000000;
}

.about__cloused:hover svg {
  fill: $dark-btn-hover;
}

.about__modal {
  position: fixed;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%) scale(1);
  width: 280px;
  height: auto;

  background-color: #ffffff;

  box-shadow: 0px 1px 3px rgba(0, 0, 0, 0.12), 0px 1px 1px rgba(0, 0, 0, 0.14),
    0px 2px 1px rgba(0, 0, 0, 0.2);
  border-radius: 15px;
  padding: 40px;
}

.about__text-backdrop {
  font-family: $content-font;
  font-weight: 500;
  font-size: 12px;
  line-height: 1.62;
  color: #907e82;
}

.about__backdrop.about__is-hidden .modal {
  transform: translate(-50%, -50%) scale(1.1);
}

@media screen and (min-widt: 320px) {
}

@media screen and (min-width: 768px) {
  .about__modal {
    width: 528px;
    height: auto;
  }
  .about__text-backdrop {
    font-size: 14px;
    line-height: 1.76;
  }
}

@media screen and (min-width: 1200px) {
  .about__text-backdrop {
    font-size: 14px;
    line-height: 1.94;
  }
}

