sources.sass\\layout\\_customer_reviews.scss:
.customer-reviews {
  font-family: $content-font;
  font-weight: 500;
  font-size: 12px;
  line-height: calc(30.6 / 12);
  color: #000;
  text-align: center;

  @media screen and (min-width: 768px) {
    font-size: 14px;
    line-height: calc(30.24 / 14);
    letter-spacing: 0.04em;
  }
  @media screen and (min-width: 1280px) {
    font-size: 16px;
    line-height: calc(29.76 / 16);
  }

  &__container {
    display: flex;
    flex-direction: column;
    align-items: center;
    padding-top: 80px;
  }
}

.customer-img {
  margin-bottom: 30px;
  @media screen and (min-width: 768px) {
    margin-bottom: 20px;
  }
  @media screen and (min-width: 1280px) {
    margin-bottom: 15px;
  }
}

.review-wrapper {
  // outline: #c7133e 1px solid;
  position: relative;
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 43px 66px 26px 57px;
  background-color: #fafafa;
  width: 100%;
  min-height: 308px;
  margin-bottom: 52px;
  @media screen and (min-width: 768px) {
    max-width: 620px;
    min-height: 164px;
    padding: 47px 80px 34px 80px;
    margin-bottom: 42px;
  }
  @media screen and (min-width: 1280px) {
    max-width: 926px;
    padding: 47px 80px 34px 80px;
    min-height: 138px;
    margin-bottom: 34px;
  }
}

.review-wrapper::after {
  content: '';
  display: block;
  position: absolute;
  left: 60px;
  top: 30px;
  background-repeat: no-repeat;
  background-position: center;
  background-size: contain;
  background-image: url(/sprites/quotes.svg);
  width: 54px;
  height: 40px;
  z-index: 1;
  @media screen and (min-width: 768px) {
    left: 40px;
    top: 30px;
  }
  @media screen and (min-width: 1280px) {
    left: 50px;
    top: 32px;
  }
}

.text-wrap {
  position: absolute;
  width: 180px;
  z-index: 10;
  background-image: repeating-linear-gradient(
    to bottom,
    transparent,
    transparent 29px,
    #e1e1e1 29px,
    #e1e1e1 30px
  );
  // outline: 1px solid blue;
  @media screen and (min-width: 768px) {
    width: 524px;
  }
  @media screen and (min-width: 1280px) {
    width: 813px;

    background-image: repeating-linear-gradient(
      to bottom,
      transparent,
      transparent 26px,
      #e1e1e1 29px,
      #e1e1e1 17px
    );
}
}

.review-text {
  margin: 0 auto;
  // outline: 1px solid green;
  width: 157px;
  @media screen and (min-width: 768px) {
    width: 460px;
  }
  @media screen and (min-width: 1280px) {
    width: 750px;
  }
}

.about-customer {
  display: inline-block;
  font-size: 14px;
  font-weight: 500;
  line-height: calc(26.04 / 14);
  text-align: center;
  position: relative;
  margin-bottom: 30px;
  @media screen and (min-width: 768px) {
    margin-bottom: 35px;
  }
}

.about-customer::before {
  content: '...';
  color: #c7133e;
  letter-spacing: 0.3em;
  font-size: 2rem;
  position: absolute;
  top: -55px;
  left: 53%;
  transform: translate(-50%);
  //   outline: 1px solid red;
  @media screen and (min-width: 768px) {
    top: -50px;
    @media screen and (min-width: 1280px) {
      top: -55px;
    }
  }
}
// ===================== SLIDER STYLES =========================

.slick-dots {
  display: flex;
  justify-content: center;

  & li:not(:last-child) {
    margin-right: 18px;
  }

  & button {
    font-size: 0;
    display: block;
    width: 12px;
    height: 12px;
    border-radius: 50%;
    border: 1px #212121;
    background-color: #d9d9d9;
  }
}

.slick-active button {
  border-radius: 0;
  background-color: #fff;
  background-image: url(/sprites/home.svg);
  background-repeat: no-repeat;
  background-position: center;
  background-size: contain;
}

sources.sass\\layout\\_contacts.scss:
.contacts {
  margin-top: 10px;
  padding-top: 86px;
  background-image: linear-gradient(
    to bottom,
    #ffffff 17%,
    $secondary-brand-color 17%,
    $secondary-brand-color 83%
  );
  @media screen and (min-width: 768px) {
    padding-top: 119px;
    background-image: linear-gradient(
      to bottom,
      #ffffff 27%,
      $secondary-brand-color 27%,
      $secondary-brand-color 73%
    );
  }
  @media screen and (min-width: 1280px) {
    padding-top: 132px;
    background-image: url('../images/contacts/contacts-bg.png'),
      linear-gradient(
        to bottom,
        #ffffff 29%,
        $secondary-brand-color 29%,
        $secondary-brand-color 71%
      );
    background-size: contain;
    background-position: center;
    background-repeat: no-repeat;
  }
}
.contacts__list {
  margin-bottom: 54px;
  @media screen and (min-width: 768px) {
    display: flex;
    flex-wrap: wrap;
    margin: -10px -10px 71px;
  }
  @media screen and (min-width: 1280px) {
    margin: -15px -15px 71px;
  }
}
.contacts__item {
  padding: 43px 20px;
  background-color: #ffffff;
  border-radius: 24px;
  box-shadow: 0px 8px 30px 0px rgba(212, 20, 67, 0.1);

  @media screen and (min-width: 768px) {
    width: calc((100% - 6 * 10px) / 3);
    margin: 10px;
    padding: 50px 31px;
  }
  @media screen and (min-width: 1280px) {
    width: calc((100% - 6 * 15px) / 3);
    margin: 15px;
    padding: 52px 43px;
  }

  &:not(:last-child) {
    @media screen and (max-width: 767px) {
      margin-bottom: 20px;
    }
  }
}

.contacts__element {
  width: 80px;
  margin-bottom: 25px;

  background-color: $coffee-card-color;

  font-size: 12px;
  font-weight: 500;
  line-height: 1.86;
  letter-spacing: 0.04em;
  color: #ffffff;
  text-align: center;
  text-transform: uppercase;

  border-radius: 4px;
  @media screen and (min-width: 768px) {
    width: 73px;
    height: 28px;
    margin-bottom: 30px;

    font-size: 16px;
    line-height: 30px;
    border-radius: 6px;
  }

  @media screen and (min-width: 768px) {
    margin-bottom: 32px;
  }
  &--bg {
    background-color: $green-color;
    width: 130px;
    @media screen and (min-width: 768px) {
      width: 138px;
    }
  }
}

.contacts__title {
  margin-bottom: 10px;

  font-size: 12px;
  font-weight: 500;
  line-height: 1.86;
  letter-spacing: 0.04em;
  @media screen and (min-width: 768px) {
    font-size: 14px;
    margin-bottom: 14px;
  }
  @media screen and (min-width: 1280px) {
    font-size: 16px;
    margin-bottom: 10px;
  }
}

.contacts__text {
  position: relative;
  margin-bottom: 50px;

  font-size: 12px;
  font-weight: 500;
  line-height: 1.5;
  color: $dark-font-color;
  @media screen and (min-width: 768px) {
    margin-bottom: 60px;
    font-size: 14px;
    line-height: 1.66;
  }
  @media screen and (min-width: 1280px) {
    margin-bottom: 64px;
    font-size: 16px;
    line-height: 1.83;
    letter-spacing: 0.04em;
  }
  &::after {
    content: '';
    position: absolute;
    left: 0;
    bottom: -25px;
    width: 100%;
    border: 1px solid #e1e1e1;
    @media screen and (min-width: 768px) {
      bottom: -30px;
    }
    @media screen and (min-width: 1280px) {
      bottom: -32px;
    }
  }
}

// Контент с рабочими днями
.contacts__worktime-list {
  position: relative;
  margin-bottom: 50px;
  @media screen and (min-width: 768px) {
    margin-bottom: 60px;
  }
  @media screen and (min-width: 1280px) {
    margin-bottom: 64px;
  }
  &::after {
    content: '';
    position: absolute;
    left: 0;
    bottom: -25px;
    width: 100%;
    border: 1px solid #e1e1e1;
    @media screen and (min-width: 768px) {
      bottom: -30px;
    }
    @media screen and (min-width: 1280px) {
      bottom: -32px;
    }
  }
}
.contacts__worktime-item:not(:last-child) {
  margin-bottom: 10px;
  @media screen and (min-width: 768px) {
    margin-bottom: 16px;
  }
  @media screen and (min-width: 1280px) {
    margin-bottom: 15px;
  }
}

.contacts__workday {
  font-size: 12px;
  font-weight: 500;
  line-height: 1.86;
  @media screen and (min-width: 768px) {
    font-size: 14px;
  }
  @media screen and (min-width: 1280px) {
    font-size: 16px;
    letter-spacing: 0.04em;
  }
}
.contacts__worktime {
  font-size: 12px;
  font-weight: 500;
  line-height: 1.86;
  color: $secondary-font-color;
  @media screen and (min-width: 768px) {
    font-size: 14px;
  }
  @media screen and (min-width: 1280px) {
    font-size: 16px;
  }
}

// Контент с обратной связью

.connect-box__link {
  font-size: 12px;
  font-weight: 500;
  line-height: 1.5;
  color: $dark-font-color;
  @media screen and (min-width: 768px) {
    font-size: 14px;
    line-height: 1.83;
  }
  @media screen and (min-width: 1280px) {
    font-size: 16px;
  }
}

// Button в Contacts
.contacts__btn-list {
  @media screen and (min-width: 768px) {
    display: flex;
    justify-content: center;
    align-items: center;
  }
}

.contacts__btn-item:not(:last-child) {
  @media screen and (max-width: 767px) {
    margin-bottom: 10px;
  }
  @media screen and (min-width: 768px) {
    margin-right: 15px;
  }
}

.contacts__btn {
  display: block;
  margin-right: auto;
  margin-left: auto;

  min-width: 162px;
  min-height: 44px;
  padding: 14px 17px;

  font-size: 16px;
  font-weight: 700;
  line-height: 1.3;
  text-align: center;

  border-radius: 22px;
  color: #ffffff;
  background-color: $brand-btn-color;
  @media screen and (min-width: 768px) {
    min-width: 191px;
    padding: 14px 35px 13px 30px;
  }
  @media screen and (min-width: 1280px) {
    min-width: 198px;
    padding: 14px 35px;
  }

  &--light {
    background-color: #ffffff;
    color: $main-font-color;
    padding: 14px 32px;
    @media screen and (min-width: 768px) {
      min-width: 174px;
      padding: 14px 39px 13px 38px;
    }
    @media screen and (min-width: 1280px) {
      min-width: 180px;
      padding: 14px 39px;
    }
  }
}
.icon-pointer {
  margin-left: 8px;
  @media screen and (min-width: 768px) {
    margin-left: 13px;
  }
}

sources.sass\\layout\\_footer.scss:
.footer {
  background-color: $secondary-brand-color;

  &__container-main {
    padding-top: 165px;
    padding-bottom: 43px;

    @media screen and(min-width: 768px) {
      display: flex;
      justify-content: space-between;
      padding-top: 165px;
      padding-bottom: 94px;
    }

    @media screen and(min-width: 1199px) {
      padding-top: 113px;
      padding-bottom: 97px;
    }
  }

  &__main-desc {
    font-family: $decorative-font, cursive;
    text-transform: uppercase;
    color: $secondary-font-color;
    font-weight: 400;
    font-size: 18px;
    line-height: 21px;
    letter-spacing: 0.04em;
    margin-bottom: 16px;
    max-width: 232px;

    @media screen and(min-width: 768px) {
      font-weight: 400;
      font-size: 26px;
      line-height: 30px;
      max-width: 340px;
    }

    @media screen and(min-width: 1280px) {
      font-weight: 400;
      font-size: 30px;
      line-height: 34px;
      max-width: 472px;
      margin-bottom: 15px;
    }
  }

  &__second-desc {
    font-family: $decorative-font, cursive;
    text-transform: uppercase;
    color: $main-font-color;
    font-weight: 400;
    font-size: 34px;
    line-height: 39px;
    letter-spacing: 0.04em;
    margin-bottom: 43px;
    max-width: 200px;

    @media screen and(min-width: 768px) {
      font-weight: 400;
      font-size: 48px;
      line-height: 55px;
      letter-spacing: 0.06em;
      max-width: 340px;
      margin-bottom: unset;
    }

    @media screen and(min-width: 1280px) {
      font-weight: 400;
      font-size: 58px;
      line-height: 66px;
      max-width: 477px;
    }
  }

  &__container-contacts {
    display: flex;
    flex-direction: column;
    align-items: flex-end;
  }

  &__list-icons {
    display: flex;
    margin-bottom: 46px;
    @media screen and(min-width: 1280px) {
      margin-bottom: 50px;
    }
  }

  &__list-icons-item {
    width: 32px;
    height: 32px;
    &:not(:last-child) {
      margin-right: 28px;
    }

    @media screen and(min-width: 768px) {
      width: 34px;
      height: 34px;
      &:not(:last-child) {
        margin-right: 30px;
      }
    }

    @media screen and(min-width: 1280px) {
      &:not(:last-child) {
        margin-right: 25px;
      }
    }
  }

  &__list-icons-link {
    display: block;
    width: 100%;
    height: 100%;
    color: #fea5bb;
    transition: color $main-transition;

    &:hover,
    &:focus,
    &:active {
      color: $main-font-color;
    }
  }

  &__social-list-icon {
    fill: currentColor;
  }

  &__contact {
    display: flex;
    justify-content: space-between;
    align-items: baseline;
  }

  &__contact-desc,
  &__link-phone {
    font-family: $content-font;
    font-weight: 700;
    font-size: 12px;
    line-height: 22px;

    @media screen and(min-width: 768px) {
      font-weight: 700;
      font-size: 14px;
      line-height: 26px;
    }

    @media screen and(min-width: 1280px) {
      font-weight: 700;
      font-size: 16px;
      line-height: 30px;
    }
  }

  &__contact-desc {
    color: #000000;
    margin-right: 3px;
  }

  &__link-phone {
    color: $main-font-color;
    letter-spacing: 0.02em;
  }

  &__container-second {
    border-top: 1px solid #e1e1e1;
    margin: 0 auto;
  }

  &__copyright {
    font-family: $content-font;
    color: $dark-font-color;
    font-weight: 700;
    font-size: 14px;
    line-height: 21px;
    max-width: 149px;
    margin: 0 auto;
    padding-top: 22px;
    padding-bottom: 25px;
    text-align: center;

    @media screen and(min-width: 768px) {
      max-width: 276px;
      padding-top: 29px;
      padding-bottom: 18px;
    }

    @media screen and(min-width: 1280px) {
      padding-top: 9px;
      padding-bottom: 10px;
    }
  }
}
